import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux"
import { useParams } from 'react-router-dom';
import { Row, Col, Container, Form, Card } from 'react-bootstrap';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import ImageFiller from 'react-image-filler';
import staffService from "../../services/staffService";

function StaffDetails() {
  const { staffId } = useParams();
  const [staff, setStaff] = useState([]);
  const dispatch = useDispatch();
  const getStaffDetails = async () => {
    const data = await staffService.getStaffById(staffId);
		setStaff(data);
	};
  const { user } = useSelector((state) => state.auth);

  useEffect(() => {
		getStaffDetails();
	}, []);

  // const priceFormatter = (number) => {
  //   return number.toLocaleString('en-US');
  // }

  return (
    <>
      <div className="wrapper">
        <Header />
        <Container className="py-5 mt-5">
          <Row className="mt-4">
            <Col className="col-12">
              <Row>
                <Col className="col-12 col-md-6 col-lg-3 mb-4">
                  <Card>
                    <ImageFiller width={250} height={190} />
                    <Card.Body>
                      <div><a className="text-secondary text-decoration-none" href="/#">{ staff.description }</a></div>
                      <div className="fw-bold mt-2">UGX { staff.price }/- </div>
                      <div className="d-flex justify-content-between mt-3">
                        <div><button type="button" className="btn rounded-5 btn-sm px-3 btn-outline-secondary"><i className="bi bi-cart3 me-1" style={{ "fontSize": '18px' }}></i> Add To cart</button></div>
                        <div>
                          <a className="text-muted fw-bold text-decoration-none" href='/#'><i className="bi bi-heart" style={{ "fontSize": '20px' }}></i></a>
                        </div>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </Col>
          </Row>
        </Container>
        <Footer />
      </div>
    </>
  );
}

export default StaffDetails;
