import { useEffect, useState } from 'react';
import { Container, Row, Col, Table } from 'react-bootstrap';
import Header from '../../components/Header';
import Footer from '../../components/Footer';

function Home() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDataForPosts = async () => {
      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/posts?_limit=8`);
        if (!response.ok) {
          throw new Error(`HTTP error: Status ${response.status}`);
        }
        let postsData = await response.json();
        setData(postsData);
        setError(null);
      } catch (err) {
        setError(err.message);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    fetchDataForPosts();
  }, []);

  return (
    <>
      <div className="wrapper">
        <Header />
          <Container className="py-5 mt-5">
            <Row className="d-flex justify-items-center">
              <Col className="col-12 my-3">
                <div className="mb-3 fs-4">List of staff management</div>
                <ul>
                  {data &&
                    data.map(({ id, title }) => (
                      <li
                        key={id}
                        className="border-b border-gray-100 text-sm sm:text-base"
                      >
                        <NavLink
                          className={({ isActive }) => {
                            const baseClasses = 'p-4 block hover:bg-gray-100';
                            return isActive
                              ? `${baseClasses} bg-gray-100`
                              : baseClasses;
                          }}
                          to={`/posts/${id}`}
                        >
                          {title}
                        </NavLink>
                      </li>
                    ))}
                </ul>
