import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { setMessage } from "./message";
import StaffService from "../../services/authService";

export const staffRegistration = createAsyncThunk(
    "staff/registerStaff",
    async ({ name, email, password }, thunkAPI) => {
        try {
            const response = await StaffService.registerStaff(name, email, password);
            thunkAPI.dispatch(setMessage(response.data.message));
            return response.data;
        } catch (error) {
            const message =
                await (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                    error.message ||
                    error.toString();
            thunkAPI.dispatch(setMessage(message));
            return thunkAPI.rejectWithValue();
        }
    }
);

const staffRegistrationSlice = createSlice({
    name: "staff",
});

export default staffRegistrationSlice.reducer;
