import axios from "axios";

// const API_URL = "http://localhost:8000/api/";

export const API_URL = axios.create({ baseUrl: "http://localhost:8000/api" });

const registerStaff = (surname, other_names, date_of_birth, image_src) => {
  return axios.post( + "staff", {
    surname,
    other_names,
    date_of_birth,
    image_src
  })
  .then((response) => {
    return response.data;
  }, (error) => {
    console.log(error);
    return error;
  });
};

const staffService = {
  registerStaff,
};

export default staffService;
